     and UNIT_DISTINCT = #{unitDistinct,jdbcType=VARCHAR}


     and UNIT_ADDRESS = #{unitAddress,jdbcType=VARCHAR}


     and UNIT_PHONE = #{unitPhone,jdbcType=VARCHAR}


